import React, { PureComponent } from 'react';
import {
  Radar, RadarChart, PolarGrid, Legend,
  PolarAngleAxis, PolarRadiusAxis, ResponsiveContainer
} from 'recharts';
import Axios from 'axios';


const COLORS = ['#0088FE', '#00C49F', '#FFBB28', '#FF8042'];

export default class Example extends PureComponent {

  state ={
    data1:[
    {
      genre: 'Rap', A: 10, fullMark: 15, 
    },
    {
      genre: 'Rock', A: 8,  fullMark: 15, 
    },
    {
      genre: 'Hip Hop', A: 5, fullMark: 15, 
    },
    {
      genre: 'Variete', A: 13,fullMark: 15, 
    },
    {
      genre: 'Classique', A: 4,fullMark: 15, 
    },
    {
      genre: 'Rumba', A: 14, fullMark: 15, 
    },]
  };

  constructor(props){
    super(props);
    var data1 = [];
    var self=this; 
    var somme = 0; 
    Axios.get('http://localhost:3001/albums').then(res =>{
      console.log(res);

      res.data.forEach(element => {
        somme++;
        data1.push(
          {
            genre: element.genre, 
            A: somme,
            fullMark: 10
          }
        );
        
      });
    }).then(function(){
      self.setState({data1:data1});
    });
  }

  render() {
    return (
      
      <div style={{ width: '100%', height: 300 }}>
      <ResponsiveContainer>
      <RadarChart data={this.state.data1}  >
        <PolarGrid />
        <PolarAngleAxis dataKey="genre" stroke="#ffffff" />
        <PolarRadiusAxis angle={30} domain={[0, 5]} />
        <Radar name="Albums par genre" dataKey="A" stroke="#8884d8" color="color.hexString()" fill={COLORS%COLORS} fillOpacity={0.6} />
        <Legend />
      </RadarChart>
      </ResponsiveContainer></div>
    );
  }
}
