import React from 'react';
import axios from 'axios';
import "./widget.css"

export default class ArtistsList extends React.Component {

    state ={
        artistes : '',
    };

    componentDidMount (){
        axios.get('http://localhost:3001/artists/5ca645d4c5421e16d9a2b611').then(res => {console.log(res);
    this.setState({artistes : res.data});
    });
    }

    render() {
        return <ul>{this.state.artistes.map(artists => <li>{artists.nom}</li>)} </ul>;

    }
}
